glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
uniform VSARGS {
  vec2 offset;
  vec2 reverseRes;
  float yratio;
};
in vec2 a_position;
in vec2 a_corner;
in vec2 a_center;
out vec2 v_corner;
out vec2 v_center;
void main() {
  vec4 pos = vec4(a_position, 0.0, 1.0);
  gl_Position = cc_matViewProj * pos;
  v_corner = a_corner.xy * reverseRes + offset;
  v_center = a_center.xy * reverseRes + offset;
  v_corner.y *= yratio;
  v_center.y *= yratio;
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec2 v_corner;
in vec2 v_center;
uniform FSARGS {
  float radius;
};
uniform sampler2D texture;
void main()
{
  vec2 uv = v_corner;
  vec2 center = v_center;
  float mask = smoothstep(radius * 3., radius, distance(uv, center));
  vec4 col = vec4(1.0);
  col.a = mask;
  gl_FragColor = col;
}

