glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec2 a_uv0;
out vec2 v_uv0;
void main() {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_uv0.y = 1.0-v_uv0.y;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec2 v_uv0;
uniform sampler2D texture;
uniform FARGS {
    vec2 C;
    float R;
    float scale;
    float angle;
    float speed;
};
float Circle(vec2 uv, vec2 o, float r, float blur) {
    return smoothstep(0., blur, distance(uv, o) - r);
}
mat2 R2(float angle){
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c);
}
vec2 ComplexPow(vec2 z, vec2 c) {
    float zmod = sqrt(dot(z, z));
    float t = atan(z.y, z.x);
    float part1 = pow(zmod, c.x) * pow(2.718281828459045, -c.y*t);
    float exp2 = log(zmod) * c.y;
    vec2 part2 = vec2(cos(exp2), sin(exp2));
    float exp3 = c.x * t;
    vec2 part3 = vec2(cos(exp3), sin(exp3));
    vec2 part2x3 = vec2(
        part2.x * part3.x - part2.y * part3.y,
        part2.x * part3.y + part2.y * part3.x);
    return part1 * part2x3;
}
void main()
{
    vec2 uv = v_uv0.xy * 2. - 1.;
    uv *= 0.5;
    float zoom = pow(scale, fract(cc_time.x * speed));
    uv /= zoom;
    float loop = floor(cc_time.x * speed);
    uv = R2(angle * loop) * uv;
    vec2 origin = vec2(0.);
    float r = R;
    float blur = 0.01;
    float mask = 1.0;
    mat2 matR2 = R2(angle);
    vec2 spiralUV = ComplexPow(uv, C);
    vec3 finalColor = texture(texture, spiralUV * 0.5 + 0.5).rgb;
    for (int i = 0; i < 6; ++i) {
        uv = matR2 * uv * scale;
        spiralUV = ComplexPow(uv, C);
        float mask = Circle(spiralUV, origin, r, blur);
        vec3 color = texture(texture, spiralUV * 0.5 + 0.5).rgb;
        finalColor = mix(color, finalColor, mask);
    }
    gl_FragColor = vec4(finalColor, 1.0);
}

